if(just.name){
	Knows.save(just.name, editor.getValue())
  if(w = getWin(just.name)){
  	w.execute("Knows.run('"+just.name+"')")
  	justShowResult('<b style="color:green;">Saved and Updated window "'+just.name+'"</b>', true)
  } 
}else{
  just.run('add');
}

_(windowManager.windows)
  .reduce(function(memo, v, k, i){
  	v.execute("just.run('top');"); 
  	var row = memo.length-1+memo[memo.length-1].length/70|0;
  	if(!memo[row])memo[row]='';
  	//memo[row] = memo[row]+' L'+ memo[row].length +'R'+row+'&'+k;
  	memo[row] += ' #'+k;
    return memo
	},['']).join('\n')

warr = _(windowManager.windows)
  .chain()
  .map(function(v,k){return [v.object.id, k]})
	.object()
	.value()

remote.app.once('browser-window-focus', function(e, w){
	justShowResult('<b style="color:green;">'+w.id +' : '+ warr[w.id]+'</b>', true)
});
justShowResult('<b style="color:blue;">Waiting for focus on any window</b>', true)
